html:
{%- from "templates/print_formats/standard_macros.html" import add_header,
     render_field -%}

<div id="header-html" class="hidden-pdf"> {{add_header(0,1,doc,letter_head, no_letterhead) }}</div>

<br>
<div class="row">
<div class="col-xs-12" style="font-size:52px;text-align: center;color: #6d0d0d !important;padding-bottom: 25px;">Zertifikat</div> 
</div>
<br>
<div class="row">
<div class="col-xs-12" style="font-size:35px;text-align: center; padding-bottom: 40px;">Hiermit bestätigen wir,             dass</div>
</div>

<div class="row">
<div class="col-xs-12" style="font-weight: bold;font-size:35px; text-align: center;padding-bottom: 33px;">{{doc.student_name}}</div>
</div>

<div class="row">
	<div class="col-xs-12" style="font-size:30px;text-align: center;padding-bottom: 33px;">am Training</div>
</div>

<div class="row">
	{%- if doc.training_name-%}
		<div class="col-xs-12" style="font-weight: bold;font-size:30px;text-align: center;padding-bottom: 38px;">{{doc.training_name}}</div>
	{%- else -%}
		<div class="col-xs-12" style="font-weight: bold;font-size:30px;text-align: center;padding-bottom: 38px;">{{doc.item_name}}</div>
	{%- endif -%}

</div>

<div class="row">
	<div class="col-xs-12" style="font-size:27px;text-align: center;padding-bottom: 40px;">teilgenommen hat.</div>
</div>
<div class="row">
	<div class="col-xs-12" style="font-size:20px;text-align: center;">
	{%- if doc.training_center-%}
		{%- set training_center = doc.training_center-%}
	{%- else -%}
		{%- set training_center = doc.training_center_new-%}
	{%- endif -%}
	{%- set center = frappe.db.get_value("Training Center",{"name":training_center},"city") -%}
	{%- if center -%}
		{{center}},
	{%- else -%}
		Glattbrugg,
	{%- endif -%}
	&nbsp;{{frappe._(frappe.utils.get_datetime(frappe.utils.now()).strftime("%B"))}}
	{{frappe.utils.get_datetime(frappe.utils.now()).strftime("%Y")}}
	</div>

</div>



<div id="footer-html" class="hidden-pdf">
	<div class="row">
	<div class="col-xs-4"></div>		
	<div class="col-xs-4">
{%- set instructor = frappe.get_doc("Instructor",doc.instructor) -%}

{%- if instructor.signature -%}
	<img src={{frappe.db.get_value("Instructor",{"instructor_name":doc.instructor_name},"signature")}} class="img-responsive attach-image-display" style="margin: 0 auto;padding-bottom: 40px; width: 172px;height: 152px;">
{%- elif instructor.employee -%}
		{%- set employee = frappe.get_doc("Employee",instructor.employee) -%}
			{%- if employee.signature -%}
				<img src={{frappe.db.get_value("Employee",{"name":doc.employee},"signature")}} class="img-responsive attach-image-display" style="margin: 0 auto;padding-bottom: 40px;width: 172px;height: 152px;">
			{%- endif -%}
{%- endif -%}


	</div>
	<div class="col-xs-4"></div>
</div>
	<div class="row">
	<div class="col-xs-12" style="font-weight: bold;font-size:15px;text-align: center;">{{doc.instructor_name}}</div></div>
        <div class="row">
	<div class="col-xs-12" style="font-size:17px;text-align: center;">Training Manager</div>
        </div>
        <div class="row">
	<div class="col-xs-12" style="font-size:17px;text-align: center;padding-bottom: 35px;">New Horizons Training (Schweiz) AG
	</div></div>


	{% if not no_letterhead and footer %}
	<div class="letter-head-footer">
		{{ footer }}
	</div>
	{% endif %}
</div>
